mapper.update:
update community_comment
set group_no = #{group_no}
where comment_no = #{comment_no}

mapper.insert:
insert into community_comment
(
	comm_no
	, user_id
	, contents
	, date
	, state
	, group_no
	, display_order_no
) values
(
	#{comm_no}
	, #{user_id}
	, #{contents}
	, now()
	, 0
	, 0


			, #{display_order_no}


			, 0


)
